import React, { useState } from "react";


const TaskAssignment = () => {
  const [formData, setFormData] = useState({
    operator_id: "",
    admin_id: "",
    task_name: "",
    machine_id: "",
    task_location: "",
    scheduled_start_time: "",
    scheduled_end_time: "",
  });

  const handleChange = (e) => {
    setFormData({ 
      ...formData, 
      [e.target.name]: e.target.value 
    });
  };

  const handleAssign = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/tasks/add", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          task_id: `TASK_${Date.now()}`, // Generating a unique task_id
          ...formData
        })
      });

      const result = await response.json();

      if (response.ok) {
        alert("✅ Task assigned successfully!");
        console.log("Assigned Task:", result.task);
        setFormData({
          operator_id: "",
          admin_id: "",
          task_name: "",
          machine_id: "",
          task_location: "",
          scheduled_start_time: "",
          scheduled_end_time: "",
        });
      } else {
        alert("❌ Failed to assign task: " + result.error);
      }
    } catch (error) {
      console.error("Error:", error);
      alert("⚠️ Error while assigning task.");
    }
  };

  return (
    <div>
      <AdminNavbar />
      <div className="admin-container">
        <h2 className="admin-title">Assign Task to Operator</h2>
        <form className="task-form">
          <input
            type="text"
            name="admin_id"
            placeholder="Admin ID"
            value={formData.admin_id}
            onChange={handleChange}
            className="input-field"
          />

          <input
            type="text"
            name="operator_id"
            placeholder="Operator ID"
            value={formData.operator_id}
            onChange={handleChange}
            className="input-field"
          />

          <input
            type="text"
            name="machine_id"
            placeholder="Machine ID"
            value={formData.machine_id}
            onChange={handleChange}
            className="input-field"
          />

          <input
            type="text"
            name="task_name"
            placeholder="Task Name"
            value={formData.task_name}
            onChange={handleChange}
            className="input-field"
          />

          <input
            type="text"
            name="task_location"
            placeholder="Task Location"
            value={formData.task_location}
            onChange={handleChange}
            className="input-field"
          />

          <label className="input-label">Scheduled Start Time</label>
          <input
            type="datetime-local"
            name="scheduled_start_time"
            value={formData.scheduled_start_time}
            onChange={handleChange}
            className="input-field"
          />

          <label className="input-label">Scheduled End Time</label>
          <input
            type="datetime-local"
            name="scheduled_end_time"
            value={formData.scheduled_end_time}
            onChange={handleChange}
            className="input-field"
          />

          <button type="button" onClick={handleAssign} className="submit-btn">
            Assign Task
          </button>
        </form>
      </div>
    </div>
  );
};

export default TaskAssignment;
