import { useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";


export default function OperatorDashboard() {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    // const operatorId = localStorage.getItem('operator_id');
    // console.log("operator_id from localStorage:", localStorage.getItem("operator_id"));
    if (!operator_id) return;

    axios.get(`http://localhost:5000/api/tasks?operator_id=${operator_id}`)
      .then(res => setTasks(res.data))
      .catch(err => console.error(err));
  }, [operator_id]);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Operator Dashboard</h2>

      <div className="flex space-x-4 mb-6">
        {['Assigned Tasks', 'Machine Details', 'Notifications', 'E-learning'].map((tab, index) => (
          <button key={index} className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
            {tab}
          </button>
        ))}
      </div>

      <h3 className="text-xl font-semibold mb-2">Assigned Tasks</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {tasks.map((task) => (
          <div key={task.task_id} className="bg-white p-4 rounded-lg shadow-md border">
            <h4 className="text-lg font-semibold mb-1">{task.task_name}</h4>
            <p><strong>Task ID:</strong> {task.task_id}</p>
            <p><strong>Machine ID:</strong> {task.machine_id}</p>
            <p><strong>Location:</strong> {task.task_location}</p>
            <p><strong>Start:</strong> {new Date(task.scheduled_start_time).toLocaleString()}</p>
            <p><strong>End:</strong> {new Date(task.scheduled_end_time).toLocaleString()}</p>
            <p className="mt-2 text-sm text-gray-600">
              Status: {task.task_status ? 'Completed' : 'Pending'}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
