import React, { useState, useEffect } from "react";
const location = useLocation();
const instructor_id = location.state?.instructor_id;

function InstructorDashboard({ instructorId }) {
  const [activeTab, setActiveTab] = useState("create");
  const [formData, setFormData] = useState({
    date: "",
    start_time: "",
    end_time: "",
  });
  const [message, setMessage] = useState("");
  const [sessions, setSessions] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch booked sessions
  useEffect(() => {
    if (activeTab === "booked") {
      setLoading(true);
      fetch("http://localhost:5000/session?instructor_id=" + instructorId)
        .then(res => res.json())
        .then(async data => {
          // Filter sessions
          const filtered = data.filter(s => s.booked && !s.completed);

          // Fetch operator emails for those sessions
          const response = await fetch("http://localhost:5000/api/auth/operators");
          const operatorList = await response.json();
          const operatorMap = Object.fromEntries(operatorList.map(op => [op.operator_id, op.email]));

          // Append email
          const enriched = filtered.map(s => ({
            ...s,
            operator_email: operatorMap[s.operator_id] || "N/A"
          }));

          setSessions(enriched);
        })
        .catch(err => console.error("Error fetching booked sessions:", err))
        .finally(() => setLoading(false));
    }
  }, [activeTab, instructorId]);

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
      e.preventDefault();
      setMessage("");

      try {
        // Combine date + time into ISO strings
        const dateStr = formData.date;
        const startDateTime = new Date(`${dateStr}T${formData.start_time}`);
        const endDateTime = new Date(`${dateStr}T${formData.end_time}`);

        const payload = {
        date: new Date(formData.date),
        start_time: new Date(`${formData.date}T${formData.start_time}`),
        end_time: new Date(`${formData.date}T${formData.end_time}`),
        instructor_id: instructorId, // from props
        booked: false,
        completed: false,
      };


        const res = await fetch("http://localhost:5000/session", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        });

        const result = await res.json();
        if (res.ok) {
          setMessage("✅ Session created!");
          setFormData({
            date: "",
            start_time: "",
            end_time: "",
            instructor_id: instructorId,
          });
        } else {
          setMessage("❌ " + (result.error || "Failed"));
        }
      } catch (err) {
        setMessage("❌ Server Error");
      }
    };
      

  const handleComplete = async (id) => {
    const confirm = window.confirm("Mark this session as completed?");
    if (!confirm) return;

    try {
      await fetch(`http://localhost:5000/session/${id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ completed: true }),
      });
      setSessions(prev => prev.filter(s => s._id !== id));
    } catch (err) {
      alert("❌ Failed to update");
    }
  };

  return (
    <div className="p-6 bg-yellow-50 min-h-screen">
      <div className="max-w-4xl mx-auto bg-white shadow-xl rounded-xl p-6">
        <div className="flex space-x-4 mb-6">
          <button
            className={`px-4 py-2 rounded font-semibold ${activeTab === "create" ? "bg-yellow-600 text-white" : "bg-gray-200"}`}
            onClick={() => setActiveTab("create")}
          >
            Create Session
          </button>
          <button
            className={`px-4 py-2 rounded font-semibold ${activeTab === "booked" ? "bg-yellow-600 text-white" : "bg-gray-200"}`}
            onClick={() => setActiveTab("booked")}
          >
            Booked Sessions
          </button>
        </div>

        {/* Create Session */}
        {activeTab === "create" && (
          <form onSubmit={handleSubmit} className="space-y-4">
            {["date", "start_time", "end_time"].map((field) => (
              <div key={field}>
                <label className="block font-medium mb-1 capitalize">{field.replace("_", " ")}:</label>
                <input
                  type={field.includes("time") ? "time" : field === "date" ? "date" : "text"}
                  name={field}
                  value={formData[field]}
                  onChange={handleChange}
                  required
                  className="w-full px-3 py-2 border border-gray-300 rounded-md"
                />
              </div>
            ))}
            <button type="submit" className="bg-yellow-600 text-white px-6 py-2 rounded-md font-semibold">
              Submit
            </button>
            {message && <div className="mt-2 font-medium text-center text-green-700">{message}</div>}
          </form>
        )}

        {/* Booked Sessions */}
        {activeTab === "booked" && (
          <div className="space-y-4">
            {loading ? (
              <p>Loading sessions...</p>
            ) : sessions.length === 0 ? (
              <p>No sessions booked yet.</p>
            ) : (
              sessions.map((session) => (
                <div key={session._id} className="bg-yellow-100 p-4 rounded-lg shadow-md">
                  <p><strong>Date:</strong> {new Date(session.date).toLocaleDateString()}</p>
                  <p><strong>Time:</strong> {new Date(session.start_time).toLocaleTimeString()} - {new Date(session.end_time).toLocaleTimeString()}</p>
                  <p><strong>Operator Email:</strong> {session.operator_email || "N/A"}</p>
                  <button
                    onClick={() => handleComplete(session._id)}
                    className="mt-2 bg-green-600 text-white px-4 py-1 rounded"
                  >
                    Mark Completed
                  </button>
                </div>
              ))
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default InstructorDashboard;
