// src/pages/Dashboard/AdminDashboard.js
import React, { useState } from 'react';
import TaskAssignment from './TaskAssignment';
import OperatingTasks from './OperatingTasks';
import AdminNavbar from './AdminNavbar';
import './admin.css';

export default function AdminDashboard() {
  const [activeTab, setActiveTab] = useState('Assign');

  return (
    <div>
      <AdminNavbar />
      <div className="admin-container">
        <h2 className="admin-title">Admin Dashboard</h2>

        {/* Tab Navigation */}
        <div className="tab-buttons">
          <button
            className={`tab-btn ${activeTab === 'Assign' ? 'active' : ''}`}
            onClick={() => setActiveTab('Assign')}
          >
            Assign Task
          </button>
          <button
            className={`tab-btn ${activeTab === 'Track' ? 'active' : ''}`}
            onClick={() => setActiveTab('Track')}
          >
            Track Tasks
          </button>
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {activeTab === 'Assign' && <TaskAssignment hideNavbar />}
          {activeTab === 'Track' && <OperatingTasks hideNavbar />}
        </div>
      </div>
    </div>
  );
}
